mapper.select:

select pid,pmphoto,pmsphoto,psub1,psub1s,psub2,psub2s,
		psub3,psub3s,phash,pprice100,pdate,ptitle from product 
		where pkind1=#{pkind1}


select pid,pmphoto,pmsphoto,psub1,psub1s,psub2,psub2s,
		psub3,psub3s,phash,pprice100,pdate,ptitle from product 
		where pkind1=#{pkind1} and pkind2=#{pkind2}
